import React, {useEffect, useState} from 'react'
import './App.css'
import {
    ClerkProvider,
    SignedIn,
    SignedOut,
    UserButton,
    useUser,
    RedirectToSignIn,
    useSession
} from '@clerk/clerk-react'
import {useNavigate} from 'react-router-dom'
import {useSupabaseToken} from './hooks/useSupabase'
import {useApi} from './hooks/useApi'
import {Header} from './components/header'
import Grid from '@mui/material/Grid'

const frontendApi = process.env.REACT_APP_CLERK_FRONTEND_API

function App() {
    const navigate = useNavigate()

    return (
        <ClerkProvider
            frontendApi={frontendApi}
            navigate={(to) => navigate(to)}
        >
            <SignedIn>
                <Homepage />
            </SignedIn>
            <SignedOut>
                <RedirectToSignIn />
            </SignedOut>
        </ClerkProvider>
    )
}

function Homepage() {
    const session = useSession()
    console.log(session)
    const api = useApi()
    const [loading, setLoading] = useState(true)
    const [items, setItems] = useState(true)

    useEffect(() => {
        const getOrCreateShoppingList = async () => {
            try {
                setLoading(true)
                let shoppingList = await api.getShoppingList()
                if (!shoppingList || shoppingList.length <= 0) {
                    shoppingList = await api.createShoppingList()
                }
                setItems(shoppingList[0].items)
            } catch (e) {
                alert(e)
            } finally {
                setLoading(false)
            }
        }
        getOrCreateShoppingList()
    }, [])

    return (
        <div>
            <Header />
            <Grid
                container
                spacing={0}
                direction="column"
                alignItems="center"
                justifyContent="center"
                style={{minHeight: '100vh'}}
            >
                <Grid item xs={3}>
                    <div>Your shopping list is empty :(</div>
                </Grid>
            </Grid>
        </div>
    )
}

export default App
