import React, {useEffect, useState} from 'react'
import './App.css'
import {
    ClerkProvider,
    SignedIn,
    SignedOut,
    UserButton,
    useUser,
    RedirectToSignIn,
    useSession
} from '@clerk/clerk-react'
import {useNavigate} from 'react-router-dom'
import {useSupabaseToken} from './hooks/useSupabase'
import {useApi} from './hooks/useApi'
import {Header} from './components/header'
import Box from '@mui/material/Box'
import Button from '@mui/material/Button'
import Stack from '@mui/material/Stack'

const frontendApi = process.env.REACT_APP_CLERK_FRONTEND_API

function App() {
    const navigate = useNavigate()

    return (
        <ClerkProvider
            frontendApi={frontendApi}
            navigate={(to) => navigate(to)}
        >
            <SignedIn>
                <Homepage />
            </SignedIn>
            <SignedOut>
                <RedirectToSignIn />
            </SignedOut>
        </ClerkProvider>
    )
}

function Homepage() {
    const session = useSession()
    console.log(session)
    const api = useApi()
    const [loading, setLoading] = useState(true)
    const [items, setItems] = useState(true)
    const [open, setOpen] = React.useState(false)
    const handleOpen = () => setOpen(true)
    const handleClose = () => setOpen(false)

    useEffect(() => {
        const getOrCreateShoppingList = async () => {
            try {
                setLoading(true)
                let shoppingList = await api.getShoppingList()
                if (!shoppingList || shoppingList.length <= 0) {
                    shoppingList = await api.createShoppingList()
                }
                setItems(shoppingList[0].items)
            } catch (e) {
                alert(e)
            } finally {
                setLoading(false)
            }
        }
        getOrCreateShoppingList()
    }, [])

    return (
        <div>
            <Header />=
            <Modal
                open={open}
                onClose={handleClose}
                aria-labelledby="modal-modal-title"
                aria-describedby="modal-modal-description"
            >
                <Box sx={style}>
                    <Typography
                        id="modal-modal-title"
                        variant="h6"
                        component="h2"
                    >
                        Text in a modal
                    </Typography>
                    <Typography id="modal-modal-description" sx={{mt: 2}}>
                        Duis mollis, est non commodo luctus, nisi erat porttitor
                        ligula.
                    </Typography>
                </Box>
            </Modal>
            <Box
                display={'flex'}
                spacing={0}
                direction="column"
                alignItems="center"
                justifyContent="center"
                style={{minHeight: '70vh'}}
            >
                <Box
                    display={'flex'}
                    spacing={0}
                    direction="column"
                    alignItems="center"
                    justifyContent="center"
                    sx={{
                        border: '1px gray solid',
                        borderRadius: '5px',
                        height: '400px',
                        width: '800px'
                    }}
                >
                    <Stack spacing={2}>
                        <Box>Your shopping list is empty :(</Box>
                        <Box
                            display={'flex'}
                            spacing={0}
                            direction="column"
                            alignItems="center"
                            justifyContent="center"
                        >
                            <Button variant="contained">
                                Add your first item
                            </Button>
                        </Box>
                    </Stack>
                </Box>
            </Box>
        </div>
    )
}

export default App
